import React,{useState} from 'react'


export default function TextForm(props) {
  const [text,setText]=useState("Text Here");
  
  const UpperCase=()=>{
    // console.log(text)
    let uppercase=text.toUpperCase()
    setText(uppercase)
  }
  const LowerCase=()=>{
    // console.log(text)
    let lowercase=text.toLowerCase()
    setText(lowercase)
  }
  const HandleChange=(event)=>{
    setText(event.target.value)  
    // console.log("Handle On change")
  }
  return (
    <div className=''>
      <form>
      <div className="mb-3">
  <label htmlFor="exampleFormControlTextarea1" className="form-label">{props.heading}</label>
  <textarea className="form-control" id="exampleFormControlTextarea1" rows="8" onChange={HandleChange} value={text}></textarea>
  </div>
  <button className="btn btn-primary" type="button" onClick={UpperCase}>Convert to Upppercase</button>
  <button className="btn btn-primary" type="button" onClick={LowerCase}>Convert to Lowercase</button>
  <button className="btn btn-primary" type="button" onClick={UpperCase}>Convert to Upppercase</button>
</form>
    </div>
  )
}
