import React, { useState } from 'react';
import Alert from './component/Alert';
import About from './component/About';
import TextForm from './component/TextForm';
import { createBrowserRouter, RouterProvider } from 'react-router-dom';
import Navbar from './component/Navbar';

function App() {
  const [mode, setMode] = useState('light');
  const [textMode, setTextMode] = useState('dark');
  const [alert, setAlert] = useState(null);

  const showAlert = (message, type) => {
    setAlert({ msg: message, type: type });
    setTimeout(() => {
      setAlert(null);
    }, 1500);
  };

  const toggleMode = () => {
    if (mode === "light") {
      setMode("dark");
      setTextMode("light");
      document.body.style.backgroundColor = "#212529";
      showAlert("Dark Mode has been enabled", "success");
      document.title = "Text Editor - Dark Edition";
    } else {
      setMode("light");
      setTextMode("dark");
      document.body.style.backgroundColor = "white";
      showAlert("Light Mode has been enabled", 'success');
      document.title = "Text Editor - Light Edition";
    }
  }

  const router = createBrowserRouter([
    {
      path: "/about",
      element: <About mode={mode} toggleMode={toggleMode} text={textMode} />
    },
    {
      path: "/",
      element: <TextForm heading="Enter Text Here" mode={mode} toggleMode={toggleMode} text={textMode}  showAlert={showAlert} />
    }
  ]);

  return (
    <RouterProvider router={router}>
      <>
        <Navbar title="Text Editor" mode={mode} toggleMode={toggleMode} text={textMode}/>
        <Alert alert={alert}/>
      </>
    </RouterProvider>
  );
}

export default App;
