import React, { useState } from "react";

export default function TextForm(props) {
  const [text, setText] = useState("");

  const capitalize = () => {
    const word = props.alert.type;
    return word.charAt(0).toUpperCase() + word.slice(1);
  }

  const UpperCase = () => {
    if (text.trim() !== "") {
      const uppercase = text.toUpperCase();
      setText(uppercase);
      props.setAlert("Converted to Uppercase", "success");
    } else {
      props.setAlert("No text to convert", "danger");
    }
  };

  const LowerCase = () => {
    if (text.trim() !== "") {
      const lowercase = text.toLowerCase();
      setText(lowercase);
      props.setAlert("Converted to Lowercase", "success");
    } else {
      props.setAlert("No text to convert", "danger");
    }
  };

  const ClearCase = () => {
    setText("");
    props.setAlert("All Text has been cleared", "success");
  };

  const HandleChange = (event) => {
    setText(event.target.value);
  };

  const WhiteSpace = () => {
    if (text.trim() !== "") {
      const newText = text.split(/\s+/).join(" ");
      setText(newText);
      props.setAlert("Deleted extra whitespaces", "success");
    } else {
      props.setAlert("No text to remove whitespaces from", "danger");
    }
  };

  const CopyCase = () => {
    if (text.trim() !== "") {
      navigator.clipboard.writeText(text);
      props.setAlert("Copied to clipboard", "success");
    } else {
      props.setAlert("No text to copy", "danger");
    }
  };

  return (
    <>
      <div className="container">
        <form>
          <div className="mb-3">
            <label htmlFor="exampleFormControlTextarea1" className={`form-label text-${props.text}`}>
              <h1>{props.heading}</h1>
            </label>
            <textarea
              className={`form-control text-${props.text} bg-${props.mode}`}
              id="exampleFormControlTextarea1"
              rows="8"
              value={text}
              onChange={HandleChange}
            ></textarea>
          </div>
          <button
            className="btn btn-primary mx-2"
            type="button"
            onClick={UpperCase}
          >
            Uppercase
          </button>
          <button
            className="btn btn-primary mx-2"
            type="button"
            onClick={LowerCase}
          >
            Lowercase
          </button>
          <button
            className="btn btn-primary mx-2"
            type="button"
            onClick={ClearCase}
          >
            Clear Text
          </button>
          <button
            className="btn btn-primary mx-2"
            type="button"
            onClick={CopyCase}
          >
            Copy Text
          </button>
          <button
            className="btn btn-primary mx-2"
            type="button"
            onClick={WhiteSpace}
          >
            Remove Extra Spaces
          </button>
        </form>
      </div>
      <div className={`container my-3 text-${props.text} bg-${props.mode}`}>
        <h1>Your text Summary</h1>
        <p>
          {text.split(/\s+/).filter((word) => word !== "").length} words and {text.length} characters
        </p>
        <p>
          The Above Text can be read in {text.split(/\s+/).filter((word) => word !== "").length * 0.008} minutes
        </p>
        <h2>Preview of Text</h2>
        <p>{text}</p>
      </div>
    </>
  );
}
