import React, { useState } from "react";

export default function TextForm(props) {
  const [text, setText] = useState("");
  console.log(props.text)
  const UpperCase = () => {
    // console.log(text)
    let uppercase = text.toUpperCase();
    setText(uppercase);
    props.showAlert("Converted to UpperCase","success")
  };
  const LowerCase = () => {
    // console.log(text)
    let lowercase = text.toLowerCase();
    setText(lowercase);
    props.showAlert("Converted to LowerCase","success")
  };
  const ClearCase = () => {
    setText(" ");
    props.showAlert("All Text has been cleared","success")
  };
  const HandleChange = (event) => {
    setText(event.target.value);
  };

  const WhiteSpace = () => {
    let newText = text.split(/[ ]+/);
    setText(newText.join(" "));
    props.showAlert("Deleted extra whitespaces","success")
  };

  const CopyCase = () => {
    navigator.clipboard.writeText(text);
    props.showAlert("Copied to clipboard","success")
  };

  
  return (
    <>
    
      <div className="container">
        <form>
          <div className="mb-3">
            <label htmlFor="exampleFormControlTextarea1" className={`form-label text-${props.text}`}>
              <h1>{props.heading}</h1>
            </label>
            <textarea
              className={`form-control text-${props.text} bg-${props.mode}`}
              id="exampleFormControlTextarea1"
              rows="8"
              onChange={HandleChange}
            ></textarea>
          </div>
          <button
            className="btn btn-primary mx-2"
            type="button"
            onClick={UpperCase}
          >
            Convert to Upppercase
          </button>
          <button
            className="btn btn-primary mx-2"
            type="button"
            onClick={LowerCase}
          >
            Convert to Lowercase
          </button>
          <button
            className="btn btn-primary mx-2"
            type="button"
            onClick={ClearCase}
          >
            Clear Text
          </button>
          <button
            className="btn btn-primary mx-2"
            type="button"
            onClick={CopyCase}
          >
            Copy Text
          </button>
          <button
            className="btn btn-primary mx-2"
            type="button"
            onClick={WhiteSpace}
            
          >
            Remove Extra Spaces
          </button>
        </form>
      </div>
      <div className={`container my-3 text-${props.text} bg-${props.mode}`}>
        <h1>Your text Summary</h1>
        <p>
          {(text.split(" ").filter(item => item !== "")).length} words and {text.length} character
        </p>
        <p>
          The Above Text can be read in {(text.split(" ").filter(item => item !== "")).length*0.008}  minutes
        </p>
        <h2>Preview of Text</h2>
        <p>{}</p>
      </div>
    </>
  );
}
