// import About from './component/About';
import { useState } from 'react';
import Navbar from './component/Navbar';
import TextForm from './component/TextForm';
import Alert from './component/Alert';

function App() {
  const [mode,setMode]=useState('light')
  const [textMode,setTextMode]=useState('dark')
  const [alert,setAlert]=useState(null)

  const showAlert=(message,type)=>{
    setAlert(
      {
        msg:message,
        type:type
      }
    )

  }

  const toggleMode=()=>{
    if(mode==="light")
    {
      setMode("dark")
      setTextMode("light")
      document.body.style.backgroundColor="#212529"
      setAlert("Dark Mode has been enabled")
    }
    else{
      setMode("light")
      setTextMode("dark")
      document.body.style.backgroundColor="white"
      setAlert("Light Mode has been enabled")
    }
  }
  return (
    <>
      <Navbar title="Text" mode={mode} toggleMode={toggleMode} text={textMode}/>
      <Alert alert={alert}/>
      <div className="container">
        <TextForm heading="Enter Text Here" mode={mode} toggleMode={toggleMode} text={textMode}/>
      </div>
      <div className="container my-2">

      {/* <About/> */}
      </div>

    </>
  );
}

export default App;
