import React, { useState } from "react";

export default function TextForm(props) {
  const [text, setText] = useState("Text Here");
  console.log(props.text)
  const UpperCase = () => {
    // console.log(text)
    let uppercase = text.toUpperCase();
    setText(uppercase);
  };
  const LowerCase = () => {
    // console.log(text)
    let lowercase = text.toLowerCase();
    setText(lowercase);
  };
  const ClearCase = () => {
    setText(" ");
  };
  const HandleChange = (event) => {
    setText(event.target.value);
    // console.log("Handle On change")
  };

  const WhiteSpace = () => {
    let newText = text.split(/[ ]+/);
    setText(newText.join(" "));
  };

  const CopyCase = () => {
    navigator.clipboard.writeText(text);
  };
  return (
    <>
      <div className="container">
        <form>
          <div className="mb-3">
            <label htmlFor="exampleFormControlTextarea1" className="{`form-label text-${props.text}`}">
              {props.heading}
            </label>
            <textarea
              className={`form-control text-${props.text}`}
              id="exampleFormControlTextarea1"
              rows="8"
              onChange={HandleChange}
              value={text}
              
            ></textarea>
          </div>
          <button
            className="btn btn-primary mx-2"
            type="button"
            onClick={UpperCase}
          >
            Convert to Upppercase
          </button>
          <button
            className="btn btn-primary mx-2"
            type="button"
            onClick={LowerCase}
          >
            Convert to Lowercase
          </button>
          <button
            className="btn btn-primary mx-2"
            type="button"
            onClick={ClearCase}
          >
            Clear Text
          </button>
          <button
            className="btn btn-primary mx-2"
            type="button"
            onClick={CopyCase}
          >
            Copy Text
          </button>
          <button
            className="btn btn-primary mx-2"
            type="button"
            onClick={WhiteSpace}
          >
            Remove Extra Spaces
          </button>
        </form>
      </div>
      <div className="container my-3">
        <h1>Your text Summary</h1>
        <p>
          {text.split(" ").length} words and {text.length} character
        </p>
        <p>
          The Above Text can be read in {text.split(" ").length * 0.008} minutes
        </p>
        <h2>Preview of Text</h2>
        <p>{text}</p>
      </div>
    </>
  );
}
