import About from './component/About';
import { useState } from 'react';
import Navbar from './component/Navbar';
// import TextForm from './component/TextForm';
import Alert from './component/Alert';
import {
  createBrowserRouter,
  RouterProvider,
} from "react-router-dom";

function App() {
  const [mode,setMode]=useState('light')
  const [textMode,setTextMode]=useState('dark')
  const [alert,setAlert]=useState(null)

  const showAlert=(message,type)=>{
    setAlert(
      {
        msg:message,
        type:type
      }
    )
    setTimeout(() => {
      setAlert(null)
    }, 1500);
  }

  const toggleMode=()=>{
    if(mode==="light")
    {
      setMode("dark")
      setTextMode("light")
      document.body.style.backgroundColor="#212529"
      showAlert("Dark Mode has been enabled","success");
      document.title="Text Editor-Dark Edition"

    }
    else{
      setMode("light")
      setTextMode("dark")
      document.body.style.backgroundColor="white"
      showAlert("Light Mode has been enabled", 'success');
      document.title="Text Editor-Light Edition"
    }
  }

  const router = createBrowserRouter([
    {
      path:"about",
      element: <About mode={mode} toggleMode={toggleMode} text={textMode}/>
    },
    {
      path:"home",
      element: <TextForm heading="Enter Text Here" mode={mode} toggleMode={toggleMode} text={textMode}  showAlert={showAlert} />
    }
  ]);


  return (
    <>
      <Navbar title="Text Editor" mode={mode} toggleMode={toggleMode} text={textMode}/>
      <Alert alert={alert}/>
      <RouterProvider/>
      {/* <div className="container">
        <TextForm heading="Enter Text Here" mode={mode} toggleMode={toggleMode} text={textMode}  showAlert={showAlert} />
      </div> */}
      <div className="container my-2">

      <About mode={mode} toggleMode={toggleMode} text={textMode}/>
      </div>

    </>
  );
}

export default App;
